mapper.select:
SELECT e.id, e.promotionId, e.storeId, i.NAME AS storeName, mg.name AS storeGradeName, p.applyStartTime AS applyStartTime, p.applyEndTime AS applyEndTime, e.state FROM t_pro_promotion_entry e LEFT JOIN t_pro_promotion p ON e.promotionId = p.id LEFT JOIN t_mer_store_info i ON e.storeId = i.id LEFT JOIN t_mer_merchant_grade mg ON i.merchantGradeId = mg.id WHERE e.deletedFlag = 0 AND p.deletedFlag = 0 AND i.deletedFlag = 0 AND e.promotionId = #{promotionId}
AND i.NAME = #{storeName}
AND mg.id = #{storeGradeId}
AND e.state = #{state}
AND p.applyStartTime >= STR_TO_DATE(#{applyStartTime},'%Y-%m-%d %H:%i:%s')
AND p.applyEndTime <= STR_TO_DATE(#{applyEndTime},'%Y-%m-%d %H:%i:%s')
