mapper.select:
SELECT ttso.id AS orderId, ttso.orderTime AS orderTime, ttso.storeId AS storeId, tmsi.merchantId as merchantId, tmsi.name AS storeName, ttoa.name AS customerName, ttoa.telephone AS customerPhone, ttso.orderState AS orderState, ttso.lastPrice AS lastPrice, ttso.orderType AS orderType, ttso.actualFreight AS actualFreight, ttso.lastModifiedTime from t_tsa_store_order AS ttso
LEFT JOIN t_tsa_store_order_detail AS ttsod ON ttso.id = ttsod.orderId
LEFT JOIN t_mer_store_info AS tmsi ON ttso.storeId = tmsi.id LEFT JOIN t_tsa_order_address AS ttoa ON ttso.id = ttoa.orderId WHERE ttso.deletedFlag = 0
AND ttso.orderType = #{orderType}
AND ttso.id = #{id}
AND ttso.orderState = #{orderState}
AND ttso.storeId = #{belongStore}
AND ttsod.deletedFlag = 0 AND LOCATE(#{goodsName},ttsod.name) > 0
ORDER BY ttso.lastModifiedTime DESC,ttso.orderTime DESC

SELECT ttso.id AS orderId, ttso.orderState AS orderState, ttso.refundState AS refundState, ttso.storeId AS storeId, ttso.actualFreight AS actualFreight, ttso.lastPrice AS lastPrice, ttso.orderTime AS orderTime, ttso.lastModifiedTime, ttso.totalOrderNo AS totalOrderNo, ttso.memberId from t_tsa_store_order AS ttso WHERE ttso.deletedFlag = 0
AND ttso.memberId = #{memberId}
AND TTSO.orderState IN
#{item}
ORDER BY ttso.lastModifiedTime DESC,ttso.orderTime DESC

SELECT ttso.id AS orderId, ttso.orderState AS orderState, ttso.storeId AS storeId, ttso.actualFreight AS actualFreight, ttso.lastPrice AS lastPrice, ttso.orderTime AS orderTime, ttso.lastModifiedTime, ttso.memberId from t_tsa_store_order AS ttso WHERE ttso.deletedFlag = 0
AND ttso.memberId = #{memberId}
AND TTSO.orderState IN
#{item}
ORDER BY ttso.lastModifiedTime DESC,ttso.orderTime DESC

SELECT DISTINCT so.id,so.orderTime,so.orderState,so.totalFreight,so.storeDeduction,so.orderType, so.lastPrice,so.deliveryType,so.storeId,so.orderTotal,so.actualFreight, si.name as storeName,t.platformDeduction, oa.name as consigneeName,oa.telephone,oa.landline,mi.userName FROM t_tsa_store_order so LEFT JOIN t_tsa_order_address oa ON so.id=oa.orderId LEFT JOIN t_mer_store_info si ON so.storeId=si.id LEFT JOIN t_mem_member mi ON so.memberId=mi.memberCode LEFT JOIN t_tsa_total_order t ON t.id=so.totalOrderNo WHERE so.deletedFlag=0
AND so.id=#{orderId}
AND so.orderState=#{orderState}
AND (so.orderState=3 OR so.orderState=4)
AND oa.name=#{userName}
AND so.deliveryType=#{deliveryType}
AND so.id IN (SELECT DISTINCT orderId FROM t_tsa_store_order_detail WHERE name=#{goodsName})
AND so.storeId=#{storeId}
AND so.orderType=1
AND so.memberId=#{memberId}
ORDER BY so.orderTime DESC

SELECT DISTINCT so.id,so.orderTime,so.orderState,so.totalFreight,so.storeDeduction,so.orderType, so.lastPrice,so.deliveryType,so.storeId,so.orderTotal,so.actualFreight, si.name as storeName,t.platformDeduction, oa.name as consigneeName,oa.telephone,oa.landline,mi.userName FROM t_tsa_store_order so LEFT JOIN t_tsa_order_address oa ON so.id=oa.orderId LEFT JOIN t_mer_store_info si ON so.storeId=si.id LEFT JOIN t_mem_member mi ON so.memberId=mi.memberCode LEFT JOIN t_tsa_total_order t ON t.id=so.totalOrderNo WHERE so.deletedFlag=0
AND so.id=#{orderId}
AND so.orderState=#{orderState}
AND (so.orderState=3 OR so.orderState=4)
AND oa.name=#{userName}
AND so.deliveryType=#{deliveryType}
AND so.id IN (SELECT DISTINCT orderId FROM t_tsa_store_order_detail WHERE name=#{goodsName})
AND so.storeId=#{storeId}
AND so.orderType=2
AND so.memberId=#{memberId}
ORDER BY so.orderTime DESC

